INSERT INTO `up_article` VALUES ('1', '深海之寻', '0', '0', '/resources/images/1478510751OFJ9.jpg', '0', '深海之寻', '深海之寻——李健', '<p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">这无穷宇宙谁能看得透</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">不知不觉中你已经远走</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">我以为自由 是随意的拥有</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">可是到最后 快一无所有</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">我的辉煌让你黯淡了很久</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">我的挥洒穷尽了你的所有</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">在那拥挤的天空何止乌云密布</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">我已经深陷迷途还不清楚</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial; font-size: 13px; white-space: normal;\">竟义无反顾</p><p style=\"margin-top: 0px; margin-bottom: 1.5em; padding: 0px; text-indent: 2em; font-family: &quot;microsoft yahei&quot;, Tahoma, Arial