INSERT INTO `up_article` VALUES ('6', '漂浮的家', '0', '0', '/resources/images/14785742915WRo.jpg', '0', '漂浮的家', '漂浮的家\n\n作词:朱婧汐\n作曲:赵照\n演唱:老狼', '<p style=\"text-align: center;\"><span style=\"font-family: 宋体, SimSun; font-size: 18px;\"><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">一切看上去很好今天</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">却也没什么好过昨天</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">我回到了熟悉的房间</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">关上门</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">打开沉默</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">打开沉默</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">掩饰寂寞</span><br/><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">一切看上去很美今天</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">你自信的笑着多耀眼</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">可你站在同一面镜子前</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">卸了妆</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">麻木的脸</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">麻木的脸</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">千人一面</span><br/><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">我们像离弓的箭</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">失去归处的飞蛾</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">家是什么</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">浮在半空的灯火</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">远方的梦汇成河</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">城市霓虹色的漩涡</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">我还相信着流星不落</span><br/><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">城市的光在诱惑</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">失去归处的飞蛾</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">家是什么浮在半空的灯火</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">远方的梦汇成河</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">城市霓虹色的漩涡</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">我想用一切换给你</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">温暖的窝</span><br/><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">一切看上去很好今天</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">这也没什么好过昨天</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">我回到了熟悉的房间</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">关上门</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">打开沉默</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">打开沉默</span><br/><span style=\"font-family: 宋体, SimSun; color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">掩饰寂寞</span></span></p>', '0', '0', '1', '0', '0', '0', '1000000', '3', '2016-11-08 11:04:27', '2016-12-22 05:39:16', '', '0');

